/**
 * Enumeration of valid key mods (possibly OR'd together).
 */
typedef enum {
    ASL_KMOD_NONE = 0x0000,
    ASL_KMOD_LSHIFT = 0x0001,
    ASL_KMOD_RSHIFT = 0x0002,
    ASL_KMOD_LCTRL = 0x0040,
    ASL_KMOD_RCTRL = 0x0080,
    ASL_KMOD_LALT = 0x0100,
    ASL_KMOD_RALT = 0x0200,
    ASL_KMOD_LGUI = 0x0400,
    ASL_KMOD_RGUI = 0x0800,
    ASL_KMOD_NUM = 0x1000,
    ASL_KMOD_CAPS = 0x2000,
    ASL_KMOD_MODE = 0x4000,
    ASL_KMOD_RESERVED = 0x8000
} ASLKeymod;

#define ASL_KMOD_CTRL (ASL_KMOD_LCTRL | ASL_KMOD_RCTRL)
#define ASL_KMOD_SHIFT (ASL_KMOD_LSHIFT | ASL_KMOD_RSHIFT)
#define ASL_KMOD_ALT (ASL_KMOD_LALT | ASL_KMOD_RALT)
#define ASL_KMOD_GUI (ASL_KMOD_LGUI | ASL_KMOD_RGUI)

